      REAL FUNCTION EPSLN (L)
C--------------------------------------------------------------------
C     THE EVALUATION OF LN(EPS) WHERE EPS IS THE SMALLEST NUMBER
C     SUCH THAT 1.0 + EPS .GT. 1.0 .  L IS A DUMMY ARGUMENT.
C--------------------------------------------------------------------
      INTEGER B
      REAL LNB
C
      B = IPMPAR(4)
      IF (B .NE. 2) GO TO 10
         LNB = .69314718055995
         GO TO 50
   10 IF (B .NE. 8) GO TO 20
         LNB = 2.0794415416798
         GO TO 50
   20 IF (B .NE. 16) GO TO 30
         LNB = 2.7725887222398
         GO TO 50
   30 LNB = ALOG(FLOAT(B))
C
   50 M = 1 - IPMPAR(5)
      EPSLN = M * LNB
      RETURN
      END
