      REAL FUNCTION CBRT (X)
C-----------------------------------------------------------------------
C                   CUBE ROOT OF A REAL NUMBER
C-----------------------------------------------------------------------
      IF (X) 30, 10, 20
   10 CBRT = 0.0
      RETURN
   20 R = ALOG(X)/3.0
      CBRT = EXP(R)
      RETURN
   30 R = ALOG(-X)/3.0
      CBRT = -EXP(R)
      RETURN
      END
      DOUBLE PRECISION FUNCTION DCBRT (X)
C-----------------------------------------------------------------------
C                   CUBE ROOT OF A REAL NUMBER
C-----------------------------------------------------------------------
      DOUBLE PRECISION X, R
C
      IF (X) 30, 10, 20
   10 DCBRT = 0.D0
      RETURN
   20 R = DLOG(X)/3.D0
      DCBRT = DEXP(R)
      RETURN
   30 R = DLOG(-X)/3.D0
      DCBRT = -DEXP(R)
