      T = A*X
      IF (IND .NE. 0) GO TO 20
      IF (X .LT. -8.0) GO TO 10
      PNDF = 0.5*ERFC1(0,-T)
      RETURN
   10 PNDF = C/ERFC1(1,-T)
      RETURN
   20 IF (X .GT. 8.0) GO TO 30
      PNDF = 0.5*ERFC1(0,T)
      RETURN
