C
C           COEFFICIENTS FOR R(6,6) APPROXIMATION,
C           IN J-FRACTION FORM, USED FOR ABS(X) .GT. 5.0
C
C---------------
      DATA P4(1)/-.315576735766984E+02/, P4(2)/-.100791496592972E+02/,
     *     P4(3)/-.710713709224200E+01/, P4(4)/-.596879853243925E+01/,
