      FRAC = P4(7) + FRAC
      DAW = (0.5E0 + 0.5E0 * W2 * FRAC) / X
      GO TO 1000
C
C     ---------- XLARGE .LT. ABS(X) ----------
C
  500 DAW = 0.5E0 / X
      GO TO 1000
C
C     ---------- RETURN FOR SMALL X ----------
C
  600 DAW = X
C
 1000 RETURN
      END
