     *     A(62) / .132887671664218E+08/, A(63) /-.278561812808645E+07/,
     *     A(64) / .308186404612662E+06/, A(65) /-.138860897537170E+05/,
     *     A(66) / .110017140269247E+03/
      DATA A(67) /-.493292536645100E+08/, A(68) / .325573074185766E+09/,
