      DATA A(121)/-.601972341723401E+13/, A(122)/ .541775107551060E+14/,
     *     A(123)/-.221349638702525E+15/, A(124)/ .542739664987660E+15/,
     *     A(125)/-.889496939881026E+15/, A(126)/ .102695519608276E+16/,
