     *                  + BN1)*X + BN0) /
     *     (((((BD5*X + BD4)*X + BD3)*X + BD2)*X + BD1)*X + BD0)
      RETURN
C
   30 RTX = SQRT(X)
      IF (X .GT. X0) GO TO 40
