      JMAX = MIN0(M, N)
      DO 10 J = 1,JMAX
         B(LB) = A(LA)
         LA = LA + KA
         LB = LB + KB
   10 CONTINUE
      IF (JMAX .EQ. N) RETURN
C
      MP1 = M + 1
      DO 20 J = MP1,N
         B(LB) = 0.D0
         LB = LB + KB
   20 CONTINUE
      RETURN
      END
      SUBROUTINE PADD (A,KA,L,B,KB,M,C,KC,N)
C     ------------------------------------------------------------------
C     ADDITION OF REAL POLYNOMIALS
C     ------------------------------------------------------------------
      REAL A(*), B(*), C(*)
C
      LA = 1
      LB = 1
      LC = 1
      DO 10 I = 1,N
         C(LC) = 0.0
         IF (I .LE. L) C(LC) = A(LA)
         IF (I .LE. M) C(LC) = C(LC) + B(LB)
         LA = LA + KA
         LB = LB + KB
         LC = LC + KC
   10 CONTINUE
      RETURN
      END
