      SUBROUTINE MCVSF(A,KA,N,B)
      REAL A(KA,N),B(*)
C
      A(1,1) = B(1)
      IF (N .LT. 2) RETURN
      L = (N*(N + 1))/2
C
      J = N
      DO 11 JJ = 2,N
      I = J
         DO 10 II = 1,J
         A(I,J) = B(L)
         I = I - 1
   10    L = L - 1
   11 J = J - 1
