      DO 12 J=1,L
      DO 11 I=1,N
      S = 0.D0
      DO 10 K=1,M
   10 S = S + DBLE(A(K,I))*DBLE(B(K,J))
   11 C(I,J) = S
   12 CONTINUE
      RETURN
      END
      SUBROUTINE SMPROD(M,N,A,KA,B)
      REAL A(KA,N),B(*)
      DOUBLE PRECISION S
      II=1
      DO 12 I=1,N
      DO 11 J=1,I
      S = 0.D0
      DO 10 K=1,M
   10 S = S + DBLE(A(K,I))*DBLE(A(K,J))
      B(II) = S
   11 II = II + 1
   12 CONTINUE
      RETURN
      END
      SUBROUTINE KPROD(A,KA,M,N,B,KB,K,L,C,KC)
C     ******************************************************************
C     KRONECKER PRODUCT OF REAL MATRICES A AND B
C     ******************************************************************
      REAL A(KA,N),B(KB,L),C(KC,*)
      INTEGER R,S
C
      J = 0
      DO 40 S = 1,N
         DO 30 JJ = 1,L
         J = J + 1
C
C        COMPUTE THE J-TH COLUMN OF C
C
            I = 0
            DO 20 R = 1,M
               DO 10 II = 1,K
               I = I + 1
   10          C(I,J) = A(R,S)*B(II,JJ)
   20       CONTINUE
C
   30    CONTINUE
   40 CONTINUE
      RETURN
      END
      SUBROUTINE DKPROD(A,KA,M,N,B,KB,K,L,C,KC)
