      IF (ANORM .GT. FACTOR) GO TO 20
C
C                NORMALIZE THE MATRIX A
C
      DO 31 J = 1,N
         DO 30 I = 1,N
   30    A(I,J) = A(I,J)/FACTOR
   31 CONTINUE
C
   40 NP1 = N + 1
      NP10 = N + 10
      DO 100 J = 1,N
C
C     COMPUTE THE J-TH COLUMN OF THE FIRST 12 POWERS OF A
C
         DO 51 I = 1,N
         S = 0.D0
            DO 50 L = 1,N
   50       S = S + A(I,L)*A(L,J)
   51    WK(I,NP1) = S
C
         DO 70 K = NP1,NP10
