      SUBROUTINE MCVSB (A,IA,JA,M,N,B,KB,NB,ML,MU,IERR)
C-----------------------------------------------------------------------
C                CONVERSION OF REAL MATRICES FROM SPARSE
C                            TO BANDED FORM
C-----------------------------------------------------------------------
      REAL A(*), B(KB,NB)
      INTEGER IA(*), JA(*)
C
C     COMPUTATION OF ML AND MU
C
      ML = 0
      MU = 0
      DO 11 I = 1,M
         LMIN = IA(I)
         LMAX = IA(I + 1) - 1
         IF (LMIN .GT. LMAX) GO TO 11
         DO 10 L = LMIN,LMAX
            IF (A(L) .EQ. 0.0) GO TO 10
            K = JA(L) - I
            MU = MAX0(MU,K)
            ML = MAX0(ML,-K)
   10    CONTINUE
   11 CONTINUE
C
C     SET B = 0 IF B PROVIDES SUFFICIENT STORAGE
C
      KMAX = ML + MU + 1
      IF (KMAX .GT. NB) GO TO 40
C
      IERR = 0
      DO 21 K = 1,KMAX
         DO 20 I = 1,M
   20       B(I,K) = 0.0
   21 CONTINUE
C
C     STORE THE MATRIX IN B
C
      J0 = ML
      DO 31 I = 1,M
         LMIN = IA(I)
         LMAX = IA(I + 1) - 1
         IF (LMIN .GT. LMAX) GO TO 31
         DO 30 L = LMIN,LMAX
            IF (A(L) .EQ. 0.0) GO TO 30
            K = JA(L) + J0
            B(I,K) = A(L)
   30    CONTINUE
         J0 = J0 - 1
   31 CONTINUE
      RETURN
C
C     ERROR RETURN
C
   40 IERR = KMAX
      RETURN
      END
