C-----------------------------------------------------------------------
      COMPLEX A(KA,*)
      REAL B(KB,L)
C
C     COMPUTATION OF NL AND NU
C
      NL = ML
      IF (ML .EQ. 0) GO TO 30
      IMIN = ML + 1
      DO 20 J = 1,ML
         DO 10 I = IMIN,M
            IF (REAL(A(I,J)) .NE. 0.0) GO TO 30
   10    CONTINUE
         NL = NL - 1
         IMIN = IMIN - 1
   20 CONTINUE
C
   30 NU = MU
      IF (MU .EQ. 0) GO TO 60
      J = ML + MU + 1
      DO 50 K = 1,MU
         DO 40 I = 1,M
            IF (REAL(A(I,J)) .NE. 0.0) GO TO 60
   40    CONTINUE
         NU = NU - 1
         J = J - 1
   50 CONTINUE
C
C     STORE THE REAL PART OF A IN B
C
   60 NUM = NL + NU + 1
      IF (NUM .GT. L) GO TO 100
      IERR = 0
      K = ML - NL
      DO 80 J = 1,NUM
         K = K + 1
         DO 70 I = 1,M
            B(I,J) = REAL(A(I,K))
   70    CONTINUE
   80 CONTINUE
      RETURN
C
C     ERROR RETURN
C
  100 IERR = NUM
