      SUBROUTINE BCVRC (M,N,A,KA,ML,MU,B,KB,NL,NU,C,KC,L,MCL,MCU,IERR)
C-----------------------------------------------------------------------
C        COMPUTE A + BI FOR THE BANDED REAL MATRICES A AND B
C-----------------------------------------------------------------------
      REAL A(KA,*), B(KB,*)
      COMPLEX C(KC,L)
C
      MCL = MAX0(ML,NL)
      MCU = MAX0(MU,NU)
      NUM = MCL + MCU + 1
      IF (NUM .GT. L) GO TO 200
C
      IERR = 0
      K = MIN0(MU,NU) + 1
C
C     INSERT THE FIRST ABS(ML - NL) DIAGONALS
C
      IF (NL .GE. ML) GO TO 30
      JA = ML - NL
      JB = 0
      JC = JA
      JMAX = NL + K
      DO 20 J = 1,JC
         DO 10 I = 1,M
            C(I,J) = CMPLX(A(I,J),0.0)
   10    CONTINUE
   20 CONTINUE
      GO TO 60
C
   30 JA = 0
      JB = NL - ML
      JC = JB
      JMAX = ML + K
      IF (JC .EQ. 0) GO TO 60
      DO 50 J = 1,JC
         DO 40 I = 1,M
            C(I,J) = CMPLX(0.0,B(I,J))
   40    CONTINUE
   50 CONTINUE
C
C     ADDITION OF THE COMMON DIAGONALS
C
   60 DO 80 J = 1,JMAX
         JA = JA + 1
         JB = JB + 1
         JC = JC + 1
         DO 70 I = 1,M
            C(I,JC) = CMPLX(A(I,JA),B(I,JB))
   70    CONTINUE
   80 CONTINUE
C
C     INSERTION OF THE REMAINING DIAGONALS
C
      IF (NU .GE. MU) GO TO 120
      JMAX = MU - NU
      DO 110 J = 1,JMAX
         JA = JA + 1
         JC = JC + 1
         DO 100 I = 1,M
            C(I,JC) = CMPLX(A(I,JA),0.0)
  100    CONTINUE
  110 CONTINUE
      RETURN
C
  120 JMAX = NU - MU
      IF (JMAX .EQ. 0) RETURN
      DO 140 J = 1,JMAX
         JB = JB + 1
         JC = JC + 1
         DO 130 I = 1,M
            C(I,JC) = CMPLX(0.0,B(I,JB))
  130    CONTINUE
  140 CONTINUE
      RETURN
C
C     ERROR RETURN
C
  200 IERR = NUM
      RETURN
      END
      SUBROUTINE BPOSE (A, KA, M, N, ML, MU, B, KB)
C-----------------------------------------------------------------------
C                TRANSPOSITION OF REAL BANDED MATRICES
C-----------------------------------------------------------------------
      REAL A(KA,*), B(KB,*)
C
      L = ML + MU + 1
      LP1 = L + 1
      IF (MU .EQ. 0) GO TO 40
C
C     DEFINE THE FIRST MU COLUMNS OF B
C
      NDIAG = MU
      DO 31 J = 1,MU
         DO 10 I = 1,NDIAG
            B(I,J) = 0.0
   10    CONTINUE
C
         LJ = LP1 - J
         IMAX = MIN0(M,N - NDIAG)
         DO 20 I = 1,IMAX
            K = NDIAG + I
            B(K,J) = A(I,LJ)
   20    CONTINUE
C
         IF (K .EQ. N) GO TO 31
         IMIN = K + 1
         DO 30 I = IMIN,N
            B(I,J) = 0.0
   30    CONTINUE
   31    NDIAG = NDIAG - 1
C
C     DEFINE THE REMAINING COLUMNS OF B
C
   40 JMIN = MU + 1
      NDIAG = 0
      DO 61 J = JMIN,L
         LJ = LP1 - J
         IMAX = MIN0(M - NDIAG,N)
         DO 50 I = 1,IMAX
            K = NDIAG + I
            B(I,J) = A(K,LJ)
   50    CONTINUE
C
         IF (IMAX .EQ. N) GO TO 61
         IMIN = IMAX + 1
         DO 60 I = IMIN,N
            B(I,J) = 0.0
   60    CONTINUE
   61    NDIAG = NDIAG + 1
      RETURN
      END
