      SUBROUTINE BADD (M,N,A,KA,ML,MU,B,KB,NL,NU,C,KC,L,MCL,MCU,IERR)
C-----------------------------------------------------------------------
C                 ADDITION OF REAL BANDED MATRICES
C-----------------------------------------------------------------------
      REAL A(KA,*), B(KB,*), C(KC,L)
C
      IERR = 0
      IF (NL - ML) 10,30,20
C
C     INSERT THE FIRST ABS(ML - NL) DIAGONALS
C
   10 MCL = ML
      IF (ML .GE. L) GO TO 50
      JA = ML - NL
      JB = 0
      JC = JA
      JMAX = NL + 1
      DO 12 J = 1,JC
         DO 11 I = 1,M
   11       C(I,J) = A(I,J)
   12 CONTINUE
      GO TO 50
C
   20 MCL = NL
      IF (NL .GE. L) GO TO 50
      JA = 0
      JB = NL - ML
      JC = JB
      JMAX = ML + 1
      DO 22 J = 1,JC
         DO 21 I = 1,M
   21       C(I,J) = B(I,J)
   22 CONTINUE
      GO TO 50
C
C     COMPUTE MCL WHEN ML = NL
C
   30 MCL = ML
      IF (ML .EQ. 0) GO TO 40
      IMIN = ML + 1
      DO 32 J = 1,ML
         DO 31 I = IMIN,M
            IF ((A(I,J) + B(I,J)) .NE. 0.0) GO TO 40
   31    CONTINUE
         MCL = MCL - 1
         IMIN = IMIN - 1
   32 CONTINUE
C
   40 JA = ML - MCL
      JB = JA
      JC = 0
      JMAX = MCL + 1
C
   50 IF (NU - MU) 100,160,130
C
C     INSERTION OF THE REMAINING DIAGONALS WHEN MU .NE. NU
C
  100 MCU = MU
      NUM = MCL + MCU + 1
      IF (NUM .GT. L) GO TO 200
C
      JMAX = JMAX + NU
      DO 111 J = 1,JMAX
         JA = JA + 1
         JB = JB + 1
         JC = JC + 1
         DO 110 I = 1,M
            C(I,JC) = A(I,JA) + B(I,JB)
  110    CONTINUE
  111 CONTINUE
C
      JMAX = MU - NU
      DO 121 J = 1,JMAX
         JA = JA + 1
         JC = JC + 1
         DO 120 I = 1,M
  120       C(I,JC) = A(I,JA)
  121 CONTINUE
      RETURN
C
  130 MCU = NU
      NUM = MCL + MCU + 1
      IF (NUM .GT. L) GO TO 200
C
      JMAX = JMAX + MU
      DO 141 J = 1,JMAX
         JA = JA + 1
         JB = JB + 1
         JC = JC + 1
         DO 140 I = 1,M
            C(I,JC) = A(I,JA) + B(I,JB)
  140    CONTINUE
  141 CONTINUE
C
      JMAX = NU - MU
      DO 151 J = 1,JMAX
         JB = JB + 1
         JC = JC + 1
         DO 150 I = 1,M
  150       C(I,JC) = B(I,JB)
  151 CONTINUE
      RETURN
C
C     COMPUTE MCU WHEN MU = NU
C
  160 MCU = MU
      IF (MU .EQ. 0) GO TO 170
      LA = ML + MU + 1
      LB = NL + NU + 1
      DO 162 J = 1,MU
         DO 161 I = 1,M
            IF ((A(I,LA) + B(I,LB)) .NE. 0.0) GO TO 170
  161    CONTINUE
         MCU = MCU - 1
         LA = LA - 1
         LB = LB - 1
  162 CONTINUE
C
C     ADDITION OF THE REMAINING COLUMNS WHEN MU = NU
C
  170 NUM = MCL + MCU + 1
      IF (NUM .GT. L) GO TO 200
C
      JMAX = JMAX + MCU
      DO 181 J = 1,JMAX
         JA = JA + 1
         JB = JB + 1
         JC = JC + 1
         DO 180 I = 1,M
            C(I,JC) = A(I,JA) + B(I,JB)
  180    CONTINUE
  181 CONTINUE
      RETURN
C
C     ERROR RETURN
C
  200 IERR = NUM
      RETURN
      END
