      SUBROUTINE BPROD (M, N, L, A, KA, ML, MU, B, KB, NL, NU,
     *                  C, KC, NC, MCL, MCU, IERR)
C-----------------------------------------------------------------------
C                MULTIPLICATION OF REAL BANDED MATRICES
C-----------------------------------------------------------------------
      REAL A(KA,*), B(KB,*), C(KC,NC)
C
      IERR = 0
      DO 11 J = 1,NC
         DO 10 I = 1,M
   10       C(I,J) = 0.0
   11 CONTINUE
C
      MLP1 = ML + 1
      NLP1 = NL + 1
      NPML = N + ML
      NPNU = N + NU
      MCL = MIN0(M - 1,ML + NL)
      JC = 0
      IF (MCL .EQ. 0) GO TO 100
C
C     FIND THE FIRST NONZERO LOWER DIAGONAL
C
      MAXD = MCL
      DO 31 NDIAG = 1,MAXD
         IMJ = MAXD + 1 - NDIAG
         JMAX = MIN0(L,M - IMJ,NPML - IMJ,NPNU)
         DO 21 J = 1,JMAX
            I = J + IMJ
            SUM = 0.0
            KMIN = MAX0(1,I - ML,J - NU)
            KMAX = MIN0(N,I + MU,J + NL)
            KK = MLP1 - I + KMIN
            JJ = NLP1 + J - KMIN
            DO 20 K = KMIN,KMAX
               SUM = SUM + A(I,KK)*B(K,JJ)
               KK = KK + 1
               JJ = JJ - 1
   20       CONTINUE
            C(I,1) = SUM
   21    CONTINUE
C
         DO 30 J = 1,JMAX
            I = J + IMJ
            IF (C(I,1) .NE. 0.0) GO TO 40
   30    CONTINUE
         MCL = MCL - 1
   31 CONTINUE
      GO TO 100
C
   40 IF (MCL .GE. NC) GO TO 100
      JC = 1
      IF (MCL .EQ. 1) GO TO 100
C
C     COMPUTE THE REMAINING LOWER DIAGONALS
C
      MIND = NDIAG + 1
      DO 52 NDIAG = MIND,MAXD
         JC = JC + 1
         IMJ = MAXD + 1 - NDIAG
         JMAX = MIN0(L,M - IMJ,NPML - IMJ,NPNU)
         DO 51 J = 1,JMAX
            I = J + IMJ
            SUM = 0.0
            KMIN = MAX0(1,I - ML,J - NU)
            KMAX = MIN0(N,I + MU,J + NL)
            KK = MLP1 - I + KMIN
            JJ = NLP1 + J - KMIN
            DO 50 K = KMIN,KMAX
               SUM = SUM + A(I,KK)*B(K,JJ)
               KK = KK + 1
               JJ = JJ - 1
   50       CONTINUE
            C(I,JC) = SUM
   51    CONTINUE
   52 CONTINUE
C
C     FIND THE LAST NONZERO UPPER DIAGONAL
C
  100 JC = JC + 1
      MCU = MIN0(L - 1,MU + NU)
      IF (MCU .EQ. 0) GO TO 130
C
      MAXD = MCU
      DO 121 NDIAG = 1,MAXD
         JMI = MAXD + 1 - NDIAG
         IMAX = MIN0(M,L - JMI,NPML,NPNU - JMI)
         DO 111 I = 1,IMAX
            J = I + JMI
            SUM = 0.0
            KMIN = MAX0(1,I - ML,J - NU)
            KMAX = MIN0(N,I + MU,J + NL)
            KK = MLP1 - I + KMIN
            JJ = NLP1 + J - KMIN
            DO 110 K = KMIN,KMAX
               SUM = SUM + A(I,KK)*B(K,JJ)
               KK = KK + 1
               JJ = JJ - 1
  110       CONTINUE
            C(I,JC) = SUM
  111    CONTINUE
C
         DO 120 I = 1,IMAX
            IF (C(I,JC) .NE. 0.0) GO TO 130
  120    CONTINUE
         MCU = MCU - 1
  121 CONTINUE
C
  130 LAST = MCL + MCU + 1
      IF (LAST .GT. NC) GO TO 200
      IF (MCU .EQ. 0) GO TO 140
      DO 131 I = 1,IMAX
         C(I,LAST) = C(I,JC)
         C(I,JC) = 0.0
  131 CONTINUE
C
C     COMPUTE THE MAIN DIAGONAL AND THE REMAINING UPPER DIAGONALS
C
  140 MAXD = MAX0(1,MCU)
      DO 143 NDIAG = 1,MAXD
         JMI = NDIAG - 1
         IMAX = MIN0(M,L - JMI,NPML,NPNU - JMI)
         DO 142 I = 1,IMAX
            J = I + JMI
            SUM = 0.0
            KMIN = MAX0(1,I - ML,J - NU)
            KMAX = MIN0(N,I + MU,J + NL)
            KK = MLP1 - I + KMIN
            JJ = NLP1 + J - KMIN
            DO 141 K = KMIN,KMAX
               SUM = SUM + A(I,KK)*B(K,JJ)
               KK = KK + 1
               JJ = JJ - 1
  141       CONTINUE
            C(I,JC) = SUM
  142    CONTINUE
         JC = JC + 1
  143 CONTINUE
      RETURN
C
C     ERROR RETURN
C
  200 IERR = LAST
      RETURN
      END
