C
  130 LAST = MCL + MCU + 1
      IF (LAST .GT. NC) GO TO 200
      IF (MCU .EQ. 0) GO TO 140
      DO 131 I = 1,IMAX
         C(I,LAST) = C(I,JC)
         C(I,JC) = 0.D0
  131 CONTINUE
C
C     COMPUTE THE MAIN DIAGONAL AND THE REMAINING UPPER DIAGONALS
