      SUBROUTINE CBPROD (M, N, L, A, KA, ML, MU, B, KB, NL, NU,
     *                   C, KC, NC, MCL, MCU, IERR)
C-----------------------------------------------------------------------
C             MULTIPLICATION OF COMPLEX BANDED MATRICES
C-----------------------------------------------------------------------
      COMPLEX A(KA,*), B(KB,*), C(KC,NC)
      COMPLEX SUM
C
      IERR = 0
      DO 11 J = 1,NC
         DO 10 I = 1,M
   10       C(I,J) = (0.0,0.0)
   11 CONTINUE
