C
C     COMPUTE THE FIRST ML COMPONENTS
C
      MLP1 = ML + 1
      IF (ML .EQ. 0) GO TO 20
      JMIN = MLP1
      DO 11 I = 1,ML
         KMAX = MIN0(N,I + MU)
         KK = JMIN
         SUM = 0.0
         DO 10 K = 1,KMAX
            SUM = SUM + A(I,KK)*X(K)
            KK = KK + 1
   10    CONTINUE
         Y(I) = SUM
