            SUM = SUM + A(I,KK)*X(K)
            KK = KK + 1
   21    CONTINUE
         Y(I) = SUM
   22 CONTINUE
C
C     STORE ZEROS IN THE FINAL M-IMAX COMPONENTS
C
      IF (IMAX .EQ. M) RETURN
      IMIN = IMAX + 1
      DO 30 I = IMIN,M
         Y(I) = 0.0
   30 CONTINUE
      RETURN
      END
