      SUBROUTINE SCVRC (A,IA,JA,B,IB,JB,C,IC,JC,M,N,NUM,WK,IERR)
C-----------------------------------------------------------------------
C       COMPUTE A + BI FOR THE SPARSE REAL MATRICES A AND B
C-----------------------------------------------------------------------
      REAL A(*), B(*), WK(N)
      COMPLEX C(*), T
      INTEGER IA(*), JA(*), IB(*), JB(*), IC(*), JC(*)
C-------------------------
      DO 10 J = 1,N
         WK(J) = 0.0
   10 CONTINUE
C
C                  COMPUTE THE I-TH ROW OF C
C
      IP = 1
      DO 42 I = 1,M
         IC(I) = IP
         MINA = IA(I)
         MAXA = IA(I+1) - 1
         IF (MINA .GT. MAXA) GO TO 30
         DO 20 L = MINA,MAXA
            J = JA(L)
            WK(J) = A(L)
   20    CONTINUE
C
   30    MINB = IB(I)
         MAXB = IB(I+1) - 1
         IF (MINB .GT. MAXB) GO TO 40
         DO 31 L = MINB,MAXB
            IF (B(L) .EQ. 0.0) GO TO 31
            J = JB(L)
            T = CMPLX (WK(J), B(L))
            WK(J) = 0.0
            IF (IP .GT. NUM) GO TO 50
            C(IP) = T
            JC(IP) = J
            IP = IP + 1
   31    CONTINUE
C
   40    IF (MINA .GT. MAXA) GO TO 42
         DO 41 L = MINA,MAXA
            J = JA(L)
            IF (WK(J) .EQ. 0.0) GO TO 41
            IF (IP .GT. NUM) GO TO 50
            C(IP) = CMPLX (WK(J), 0.0)
            WK(J) = 0.0
            JC(IP) = J
            IP = IP + 1
   41    CONTINUE
   42 CONTINUE
      IC(M + 1) = IP
      IERR = 0
      RETURN
C
C                        ERROR RETURN
C
   50 IERR = I
      RETURN
      END
