C-----------------------------------------------------------------------
      COMPLEX A(*), B(*)
      INTEGER IA(*), JA(*), IB(*), JB(*)
C
      MP1 = M + 1
      L = IA(1) - 1
      DO 10 I = 1,MP1
         IB(I) = IA(I) - L
   10 CONTINUE
