      SUBROUTINE RPOSE (A, IA, JA, B, IB, JB, M, N)
C-----------------------------------------------------------------------
C                 TRANSPOSING A SPARSE REAL MATRIX
C-----------------------------------------------------------------------
      REAL A(*), B(*)
      INTEGER IA(*), JA(*), IB(*), JB(*)
C
C           COMPUTE THE NUMBER OF ELEMENTS IN EACH COLUMN
C                 OF A AND STORE THE RESULTS IN IB
C
      IPMIN = IA(1)
      IPMAX = IA(M+1) - 1
      IF (IPMIN .GT. IPMAX) GO TO 40
      DO 10 J = 1,N
         IB(J) = 0
   10 CONTINUE
      DO 11 IP = IPMIN,IPMAX
         J = JA(IP)
         IB(J) = IB(J) + 1
   11 CONTINUE
C
C          COMPUTE THE ROW POINTERS OF THE TRANSPOSE MATRIX
C                AND STORE THEM IN IB(2),...,IB(N+1)
C
      NUM = IA(M+1) - IA(1) + 1
      J = N
      DO 20 JJ = 1,N
         NUM = NUM - IB(J)
         IB(J+1) = NUM
         J = J - 1
   20 CONTINUE
C
C                STORE THE I-TH ROW OF A IN B AND JB
C                  AND UPDATE THE POINTERS IN IB
C
      DO 31 I = 1,M
         IPMIN = IA(I)
         IPMAX = IA(I+1) - 1
         IF (IPMIN .GT. IPMAX) GO TO 31
         DO 30 IP = IPMIN,IPMAX
            J = JA(IP)
            JP = IB(J+1)
            JB(JP) = I
            B(JP) = A(IP)
            IB(J+1) = JP + 1
   30    CONTINUE
   31 CONTINUE
      IB(1) = 1
      RETURN
C
C                    TRANSPOSE A ZERO MATRIX A
C
   40 NP1 = N + 1
      DO 41 J = 1,NP1
         IB(J) = 1
   41 CONTINUE
      RETURN
      END
      SUBROUTINE RPOSE1 (P, A, IA, JA, B, IB, JB, M, N)
C-----------------------------------------------------------------------
C                 TRANSPOSING A SPARSE REAL MATRIX
C                 WHERE THE ROWS ARE INTERCHANGED
C-----------------------------------------------------------------------
      INTEGER P(M)
      REAL A(*), B(*)
      INTEGER IA(*), JA(*), IB(*), JB(*)
C
C           COMPUTE THE NUMBER OF ELEMENTS IN EACH COLUMN
C                 OF A AND STORE THE RESULTS IN IB
C
      IPMIN = IA(1)
      IPMAX = IA(M+1) - 1
      IF (IPMIN .GT. IPMAX) GO TO 40
      DO 10 J = 1,N
         IB(J) = 0
   10 CONTINUE
      DO 11 IP = IPMIN,IPMAX
         J = JA(IP)
         IB(J) = IB(J) + 1
   11 CONTINUE
C
C          COMPUTE THE ROW POINTERS OF THE TRANSPOSE MATRIX
C                AND STORE THEM IN IB(2),...,IB(N+1)
C
      NUM = IA(M+1) - IA(1) + 1
      J = N
      DO 20 JJ = 1,N
         NUM = NUM - IB(J)
         IB(J+1) = NUM
         J = J - 1
   20 CONTINUE
C
C                STORE THE I-TH ROW OF A IN B AND JB
C                  AND UPDATE THE POINTERS IN IB
C
      DO 31 I = 1,M
         II = P(I)
