      SUBROUTINE RNOR (IX, A, N, IERR)
C-----------------------------------------------------------------------
C                 NORMAL RANDOM NUMBER GENERATOR
C-----------------------------------------------------------------------
      REAL A(N), T(2)
C---------------------
      DATA MAX /2147483647/
C---------------------
      IF (N .LE. 0) GO TO 100
      IF (IX .LE. 0 .OR. IX .GE. MAX) GO TO 110
      IERR = 0
C
      IF (N .EQ. 1) GO TO 20
      M = N/2
      CALL RCIR1 (M, IX, A(1), 2, A(2), 2, IERR)
      M = M + M
C
C                  OBTAIN THE FIRST M VARIATES
C
      DO 10 I = 1,M,2
         U = A(I)
         V = A(I + 1)
         R = U*U + V*V
         S = SQRT(-2.0*ALOG(R)/R)
         A(I) = S*U
         A(I + 1) = S*V
   10 CONTINUE
      IF (M .EQ. N) RETURN
C
C            OBTAIN THE LAST VARIATE (WHEN N IS ODD)
C
   20 CALL RCIR1 (1, IX, T(1), 1, T(2), 1, IERR)
      R = T(1)*T(1) + T(2)*T(2)
      S = SQRT(-2.0*ALOG(R)/R)
      A(N) = S*T(1)
      RETURN
C
C                         ERROR RETURN
C
  100 IERR = 1
      RETURN
  110 IERR = 2
      RETURN
      END
